import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import OfficerAuth from './components/OfficerAuth';
import OfficerDashboard from './components/OfficerDashboard';
import authService from './services/authService';

function App() {
  const isAuthenticated = () => {
    const token = authService.getToken();
    return token ? true : false;
  };

  return (
    <div className="App">
      <Router>
        <Routes>
          {/* Combined Login/Signup Route */}
          <Route path="/auth" element={<OfficerAuth />} />

          {/* Protected Route for Officer Dashboard */}
          <Route
            path="/dashboard"
            element={isAuthenticated() ? <OfficerDashboard /> : <Navigate to="/auth" />}
          />

          {/* Redirect to login if user tries to access root */}
          <Route path="/" element={<Navigate to="/auth" />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
